<div class="right">
    <a rel="<?= $collector->getName() ?>" class="collector-clear-link" href="#">
        <img style="width:20px;"
             src="data:image/png;base64,"/>
    </a>
</div>
<h1>Cache collected data</h1>
<div id="accordion">
    <?php foreach ($collector->getData() as $key => $data) : ?>
        <?php if (!empty($data)) : ?>
            <div class="title"><?= $key ?>
                <span class="right"><?= sizeof($data) ?> cache(s)</span>
            </div>
            <div class="section">
                <table class="tablesorter">
                    <thead>
                    <tr>
                        <th>Name</th>
                        <th>Size (Ko)</th>
                        <th>Number of sets</th>
                        <th>Number of loads</th>
                    </tr>
                    </thead>
                    <tbody>

                    <?php foreach ($data as $row) : ?>
                        <tr>
                            <td>
                                <div class="tooltip collector-cache-key"
                                     title="<?= $row['name'] ?>"><?= $row['name'] ?></div>
                            </td>
                            <td class="numeric"><?= number_format($row['size'] / 1024, 5) ?></td>
                            <td class="numeric"><?= $row['set'] ?></td>
                            <td class="numeric"><?= $row['load'] ?></td>
                        </tr>
                    <?php endforeach; ?>
                    </tbody>
                </table>
            </div>
        <?php endif; ?>
    <?php endforeach; ?>
</div>
<script type="text/javascript">
    $('.tooltip').tooltip();
    $("table").tablesorter();
    $("#accordion").accordion({ collapsible: true });
    $('.collector-clear-link').clearCollector('#collector-content');
</script>